    def build_building(self):
        x, y = 0, 0

        for row in self.city_pattern:
            for col in row:
                if col == "1":
                    self.brick_list.append(pygame.Rect(x, y, 5, 5))
                if col == "2":
                    self.shade_list.append(pygame.Rect(x, y, 5, 5))
                if col == "0":
                    self.window_list.append(pygame.Rect(x, y, 5, 5))
                x += 5
            y += 5
            x = 0


    def draw(self):
        pygame.draw.rect(self.surface, cs.nineoclock["pygame"], self.nineRect)
        pygame.draw.rect(self.surface, cs.tenoclock["pygame"], self.tenRect)
        pygame.draw.rect(self.surface, cs.elevenoclock["pygame"], self.elevenRect)

        for bricks in self.brick_list:
            pygame.draw.rect(self.surface, cs.gray["pygame"], bricks)

        for shaders in self.shade_list:
            pygame.draw.rect(self.surface, cs.night_gray["pygame"], shaders)

        for windows in self.window_list:
            pygame.draw.rect(self.surface, cs.yellow["pygame"], windows)


